      if (doc.data().email == email) {

        const fname = doc.data().firstname;
        const phoneNum = doc.data().phone;
        localStorage.setItem("name", fname);
        localStorage.setItem("email", email);
        localStorage.setItem("phone", phoneNum);
        return doc.data()
      }



